sp_widget.client_script:
function() {
  return {
    controller: function($scope) {
      var c = this;

      // 定义所有可能的状态步骤
      c.allSteps = [
        { label: "New", value: "new" },
        { label: "Assess", value: "assess" },
        { label: "Authorize", value: "authorize" },
        { label: "Scheduled", value: "scheduled" },
        { label: "Implement", value: "implement" },
        { label: "Review", value: "review" },
        { label: "Closed", value: "closed" },
        { label: "Canceled", value: "canceled" }
      ];

      // 初始化步骤状态
      c.steps = angular.copy(c.allSteps);

      // 监听数据变化
      $scope.$watch('data.record.state', function(newVal) {
        if (newVal) {
          updateProgressBar(newVal);
        }
      });

      // 如果有初始数据，立即更新
      if ($scope.data.record && $scope.data.record.state) {
        updateProgressBar($scope.data.record.state);
      }

      // 更新进度条状态
      function updateProgressBar(currentState) {
        var foundCurrent = false;

        c.steps.forEach(function(step) {
          step.isActive = (step.value === currentState);
          step.isCompleted = foundCurrent ? false : (step.value !== currentState);

          if (step.isActive) {
            foundCurrent = true;
          }
        });
      }
    }
  };
}

sp_widget.css:
.status-progress-container {
  margin: 20px 0;
  position: relative;
}

.status-progress-bar {
  display: flex;
  justify-content: space-between;
  position: relative;
  z-index: 2;
}

.status-step {
  padding: 8px 16px;
  border-radius: 20px;
  background-color: #f0f0f0;
  color: #666;
  font-weight: 500;
  text-align: center;
  min-width: 80px;
  position: relative;
  transition: all 0.3s ease;
}

.status-step.active {
  background-color: #4CAF50;
  color: white;
  box-shadow: 0 2px 5px rgba(0,0,0,0.2);
}

.status-step.completed {
  background-color: #8BC34A;
  color: white;
}

.status-connector-line {
  position: absolute;
  top: 50%;
  left: 0;
  right: 0;
  height: 2px;
  background-color: #e0e0e0;
  z-index: 1;
  transform: translateY(-50%);
}

